package advent
import scala.collection.mutable.Set
object Main {
  // Bathroom Security
  def main(args: Array[String]): Unit = {
    val input = """




 """

    // Part 1
    // Find the code to the bathroom given a set of Up,Down,Left,Right instructions.
    // The bathroom input panel looks like a calculator:
    // 1 2 3
    // 4 5 6
    // 7 8 9
    // Starting initially at position 5, read each line of instructions to find the code.
    println(getCode(input))
  }

  def getCode(input: String): String = {
    var position = (1, 1)
    for (line <- input.split("\n")) {
      println(line)
      position = getPosition(line, position)
      println(position)
      println(panel(position._1)(position._2))
    }
    "1"
  }

  val panel = Array(Array(1, 4, 7), Array(2, 5, 8), Array(3, 6, 9))

  def getPosition(line: String, startPosition: (Int, Int)): (Int, Int) = {
    var position = startPosition
    for (instruction <- line) {
      instruction match {
        case 'U' => position = (position._1, Math.max(0, position._2 - 1))
        case 'D' => position = (position._1, Math.min(2, position._2 + 1))
        case 'L' => position = (Math.max(0, position._1 - 1), position._2)
        case 'R' => position = (Math.min(2, position._1 + 1), position._2)
      }
    }
    position
  }
}
